if(!isNaN(v)) {
    global.set('servo', v);
    node.status({fill:'orange',shape:'dot',text:v+'°'});
}
return null;

const temperature = global.get('temperature') || null;
const humidity = global.get('humidity') || null;
const luminosity = global.get('luminosity') || null;

msg.payload = {
    temperature,
    humidity,
    luminosity,
    timestamp: new Date().toISOString()
};

msg.headers = {
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': '*'
};

return msg;

const led1 = global.get('led1') || 'OFF';
const led2 = global.get('led2') || 'OFF';
const servo = global.get('servo') || 90;

msg.payload = {
    led1,
    led2,
    servo
};

msg.headers = {
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': '*'
};

return msg;

const body = msg.payload || {};

if(!body.device || typeof body.action === 'undefined'){
    msg.statusCode = 400;
    msg.payload = { error: 'Missing device or action' };
    msg.headers = {'Access-Control-Allow-Origin': '*'};
    return [null, msg, null];
}

const device = body.device;
const action = body.action;
let topic = '';
let mqttPayload = null;

if(device === 'led1') {
    topic = 'push/led1';
    mqttPayload = String(action);
} else if(device === 'led2') {
    topic = 'push/led2';
    mqttPayload = String(action);
} else if(device === 'servo') {
    topic = 'miot/ir/servo';
    mqttPayload = Number(action);
} else {
    msg.statusCode = 400;
    msg.payload = { error: 'Unknown device: ' + device };
    msg.headers = {'Access-Control-Allow-Origin': '*'};
    return [null, msg, null];
}

const timestamp = (new Date()).toISOString();

// Prepare MQTT message
const mqttMsg = {
    topic: topic,
    payload: mqttPayload
};

// Prepare HTTP response
const httpMsg = {
    payload: {
        ok: true,
        device: device,
        action: action,
        timestamp: timestamp
    },
    headers: {'Access-Control-Allow-Origin': '*'},
    statusCode: 200
};

// Only log servo to Firestore (LEDs toggle only via IR)
let logMsg = null;
if(device === 'servo') {
    logMsg = {
        payload: {
            fields: {
                timestamp: { timestampValue: timestamp },
                type: { stringValue: 'Servo' },
                value: { stringValue: String(action) }
            }
        }
    };
}

node.status({fill:'green',shape:'dot',text:`${device}:${action}`});

return [mqttMsg, httpMsg, logMsg];

// Only process if logMsg has fields (servo from /api/control or LED/Servo from /api/ir)
if (!msg.payload?.fields) return null;

const projectId = "miot-2a535";
const apiKey = "AIzaSyAthtPTwsME_GWtuXELBUos_9Tnws-d5to";
const collection = "dht22_states";

msg.url = `https://firestore.googleapis.com/v1/projects/${projectId}/databases/(default)/documents/${collection}?key=${apiKey}`;
msg.method = "POST";
msg.headers = { "Content-Type": "application/json" };

return msg;

const projectId = "miot-2a535";
const apiKey = "AIzaSyAthtPTwsME_GWtuXELBUos_9Tnws-d5to";
msg.url = `https://firestore.googleapis.com/v1/projects/${projectId}/databases/(default)/documents:runQuery?key=${apiKey}`;
msg.method = "POST";
msg.headers = { "Content-Type": "application/json" };
msg.payload = { structuredQuery: { from: [{ collectionId: "dht22_states" }], orderBy: [{ field: { fieldPath: "timestamp" }, direction: "DESCENDING" }], limit: 20 } };

return msg;

const rows = Array.isArray(msg.payload) ? msg.payload : [];
const docs = rows.filter(r => r.document).map(r => r.document);
const data = docs.map(doc => ({
    type: doc.fields?.type?.stringValue,
    value: doc.fields?.value?.stringValue || doc.fields?.value?.doubleValue || doc.fields?.value?.integerValue,
    timestamp: doc.fields?.timestamp?.timestampValue
}));

msg.payload = data;
msg.headers = { 'Content-Type': 'application/json', 'Access-Control-Allow-Origin': '*' };
msg.statusCode = 200;

node.status({fill:'green',shape:'dot',text:data.length + ' records'});
return msg;

msg.statusCode = 200;
msg.headers = {
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Methods': 'GET, POST, OPTIONS',
    'Access-Control-Allow-Headers': 'Content-Type'
};
msg.payload = '';
return msg;

const body = msg.payload || {};
const command = body.command || '';

const irCodes = {
    'led1_toggle': '0x00FF30CF',
    'led2_toggle': '0x00FF18E7',
    'servo_180': '0x00FF02FD',
    'servo_0': '0x00FF22DD'
};

if (!irCodes[command]) {
    msg.statusCode = 400;
    msg.payload = { error: 'Unknown IR command', available: Object.keys(irCodes) };
    return [null, msg, null];
}

const irMsg = {
    topic: 'miot/ir/code',
    payload: irCodes[command]
};

const timestamp = (new Date()).toISOString();

// Map IR commands to device types for Firestore logging
let deviceType = '';
let action = '';
if (command === 'led1_toggle') { deviceType = 'LED1'; action = 'toggle'; }
else if (command === 'led2_toggle') { deviceType = 'LED2'; action = 'toggle'; }
else if (command === 'servo_180') { deviceType = 'Servo'; action = '180'; }
else if (command === 'servo_0') { deviceType = 'Servo'; action = '0'; }

const logMsg = {
    payload: {
        fields: {
            timestamp: { timestampValue: timestamp },
            type: { stringValue: deviceType },
            value: { stringValue: action }
        }
    }
};

const httpMsg = {
    payload: {
        ok: true,
        command: command,
        code: irCodes[command],
        timestamp
    },
    statusCode: 200
};

node.status({fill:'blue',shape:'dot',text:command});
return [irMsg, httpMsg, logMsg];
